const request = require('request');

const options = {
  method: 'POST',
  url: 'http://localhost:8006/file-catalog',
  headers: {accept: 'application/json', 'content-type': 'application/json'},
  body: {
    service: 'string',
    source: 'string',
    context: 'string',
    lake_layer: 'string',
    schema_type: 'string',
    catalog: {}
  },
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

