const options = {
  method: 'POST',
  url: 'http://localhost:8001/inputs/context/%7Bcontext%7D/service/%7Bservice%7D/source/%7Bsource%7D',
  headers: {accept: 'application/json', 'content-type': 'application/json'},
  body: {data: {}},
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

